	public function uninstall() {
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faq`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faqcategory`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faqcategory_description`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faqcategory_to_layout`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faqcategory_to_store`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faq_description`");
		$this->db->query("DROP TABLE IF EXISTS `" . DB_PREFIX . "faq_to_store`");

		$this->load->model('user/user_group');
		$this->model_user_user_group->removePermission($this->user->getId(), 'access', 'extension/module/faqcategory');
		$this->model_user_user_group->removePermission($this->user->getId(), 'modify', 'extension/module/faqcategory');
		$this->model_user_user_group->addPermission($this->user->getId(), 'access', 'catalog/faq');
		$this->model_user_user_group->addPermission($this->user->getId(), 'modify', 'catalog/faq');
		$this->model_user_user_group->addPermission($this->user->getId(), 'access', 'catalog/faqcategory');	
